import React, { useEffect, useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import { getTasks } from "./services/taskService";
import './index.css'; // <- Make sure this line is added

function App() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = () => {
    getTasks()
      .then((data) => {
        setTasks(data);
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <nav className="navbar navbar-custom mb-4">
        <div className="container-fluid">
          <span className="navbar-brand mb-0 h1">ðŸŒ¸ Task Tracker</span>
        </div>
      </nav>

      <div className="container">
        <div className="card p-4">
          <h2 className="mb-4">Add New Task</h2>
          <TaskForm onTaskAdded={fetchTasks} />

          <hr />

          <h3 className="mb-3">Your Tasks</h3>
          {tasks.length === 0 ? (
            <p className="text-muted">No tasks to show</p>
          ) : (
            <TaskList tasks={tasks} onUpdate={fetchTasks} />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
