import React from "react";
import { updateTask, deleteTask } from "../services/taskService";

function TaskList({ tasks, onUpdate }) {
  return (
    <ul className="list-group">
      {tasks.map((task) => (
        <li
          key={task.id}
          className="list-group-item d-flex justify-content-between align-items-center"
        >
          <div>
            <strong>{task.title}</strong> â€“ {task.description}{" "}
            <span className="badge bg-secondary ms-2">
              {task.is_done === "1" ? "Done" : "Pending"}
            </span>
          </div>
          <div>
            <button
              className="btn btn-sm btn-outline-primary me-2"
              onClick={() =>
                updateTask({ ...task, is_done: task.is_done === "1" ? 0 : 1 }).then(onUpdate)
              }
            >
              Mark {task.is_done === "1" ? "Pending" : "Done"}
            </button>
            <button
              className="btn btn-sm btn-outline-danger"
              onClick={() => deleteTask(task.id).then(onUpdate)}
            >
              Delete
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
