import React from "react";
import { updateTask, deleteTask } from "../services/taskService";

function TaskList({ tasks, onUpdate }) {
  return (
    <ul>
      {tasks.map(task => (
        <li key={task.id}>
          <strong>{task.title}</strong> â€“ {task.description} [{task.is_done === "1" ? "Done" : "Pending"}]
          <button onClick={() => updateTask({ ...task, is_done: task.is_done === "1" ? 0 : 1 }).then(onUpdate)}>
            Mark {task.is_done === "1" ? "Pending" : "Done"}
          </button>
          <button onClick={() => deleteTask(task.id).then(onUpdate)}>Delete</button>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
